package ch02.pac03;

public class ByteOperationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte result1 = 10 + 20;
		System.out.println("Result 1 : " + result1);

		byte x = 10;
		byte y = 20;
		int result2 = x + y;
		System.out.println("Result 2 : " + result2);
	}

}
