package ch03.pac02;

public class AssignmentOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		result += 10;
		System.out.println("Result : " + result);

		result -= 5;
		System.out.println("Result : " + result);
		
		result *= 3;
		System.out.println("Result : " + result);
		
		result /= 5;
		System.out.println("Result : " + result);
		
		result %= 3;
		System.out.println("Result : " + result);
	}

}
