package ch03.pac02;

public class CharOperationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c1 = 'A' + 1;
		char c2 = 'A';

		// char c3 = c2 + 1;
		char c4 = (char) (c2 + 2);
		System.out.println("C1 : " + c1);
		System.out.println("C2 : " + c2);
		System.out.println("C4 : " + c4);
	}

}
