package ch03.pac02;

public class DenyLogicOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean play = true;
		System.out.println("Play : " + play);
		
		play = !play;
		System.out.println("Play : " + play);
		
		play = !play;
		System.out.println("Play : " + play);
		
	}

}
